sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if ( newValue == '') {
        return;
    }


    //Type appropriate comment here, and begin script below
    var whatneeded = g_form.getValue('u_what_needed'); // getting the value of what needed.

    g_form.clearOptions('u_what_needed'); // clearing all the options from what needed choice list.

    if ( newValue == 'hr') { 

		//if the value of Request Type field is hr, add 2 hr choices and other to what neededfield choice list.
        g_form.clearOptions('u_what_needed');
		g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1');
        g_form.addOption('u_what_needed', 'hr2', 'Human Resources 2');
        g_form.addOption('u_what_needed', 'hr3', 'Human Resources 3');

        g_form.addOption('u_what_needed', 'other', 'Other');

    }


    if ( newValue == 'facilities') { 
		//if the value of Request Type field is facilities, add 2 hr choices and other to what neededfield choice list.
		g_form.clearOptions( 'u_what_needed');
        g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
        g_form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
        g_form.addOption('u_what_needed', 'other', 'Other');

    }

    if ( newValue == 'legal'){	
		//if the value of Request Type field is legal, add 2 hr choices and other to what neededfield choice list.
		g_form.clearOptions('u_what_needed');
		g_form.addOption('u_what_needed', 'legal1', 'Legal 1');
        g_form.addOption('u_what_needed', 'legal2', 'Legal 2');
        g_form.addOption('u_what_needed', 'other', 'Other');

    }

	if (isLoading && !g_form.isNewRecord()) { 
		//If the form is loading and is not a new record set u_what_needed  to the value from the record before it was loaded.
        g_form.setValue('u_what_needed', whatneeded);
    }

	}
